class WFvcode{function createAuthNumImg($randStr,$imgw,$imgh,$fontName){header("content-type: image/png");$image=imagecreate($imgw,$imgh);$color_white=imagecolorallocate($image,255,255,255);$color_gray=imagecolorallocate($image,255,255,255);$color_black=imagecolorallocate($image,255,0,0);for($i=0;$i<1000;$i++){imagesetpixel($image,mt_rand(0,$imgw),mt_rand(0,$imgh),$color_gray);}imagerectangle($image,0,0,$imgw-1,$imgh-1,$color_gray);for($i=10;$i<$imgh;$i+=10)imageline($image,0,$i,$imgw,$i,$color_gray);imagettftext($image,14,0,8,19,$color_black,$fontName,$randStr);for($i=10;$i<$imgw;$i+=10)imagepng($image);imagedestroy($image);}function getCode($length,$mode){switch($mode){case '1':$str='123456789';break;case '2':$str=base64_decode('');break;default:$str='123456789';break;}$result='';if($mode==2){for($i=0;$i<$length;$i++){$result.= substr($str,mt_rand(0,250)*3,3);}return $result;}else{$l=strlen($str)-1;for($i=0;$i<$length;$i++){$num=rand(0,$l);$result.=$str[$num];}return $result;}}public function countcode(){$a=$this->GetCode(2,1);$b=$this->GetCode(2,1);$passport=$a.'+'.$b.'=?';$result=$a+$b;$_SESSION['sysvcode']=substr(md5($result),10,20);$this->createAuthNumImg($passport,88,28,'fonts/number.ttf');}public function numbercode(){$result=$this->GetCode(4,1);$_SESSION['sysvcode']=substr(md5($result),10,20);$this->createAuthNumImg($result,52,28,'fonts/number.ttf');}public function chinesecode(){$result=$this->GetCode(3,2);$_SESSION['sysvcode']=substr(md5($result),10,20);$this->createAuthNumImg($result,72,28,'fonts/chinese.ttf');}}?>